import { setProperty } from './lib/utils';
export var mapperProperty = function (key, type) {
    return setProperty({
        key: key,
        type: type
    });
};
export var deepMapperProperty = function (key, Clazz) {
    return setProperty({
        key: key,
        Clazz: Clazz
    });
};
export var filterMapperProperty = function (key, filter) {
    return setProperty({
        key: key,
        filter: filter
    });
};
